export default class PointLocator {
    static constructor_(...args: any[]): void;
    constructor(...args: any[]);
    locateInPolygonRing(p: any, ring: any): number;
    intersects(p: any, geom: any): boolean;
    updateLocationInfo(loc: any): void;
    _isIn: boolean;
    computeLocation(p: any, geom: any): void;
    locateOnPoint(p: any, pt: any): number;
    locateOnLineString(p: any, l: any): number;
    locateInPolygon(p: any, poly: any): number;
    locate(p: any, geom: any): number;
    _numBoundaries: number;
}
